requests.testScript:
pw.test("Successfully parses secret variable holding the header value", () => {
    const secretHeaderValue = pw.env.get("secretHeaderValue")
    pw.expect(secretHeaderValue).toBe("secret-header-value")

    if (secretHeaderValue) {
      pw.expect(pw.response.body.headers["Secret-Header-Key"]).toBe(secretHeaderValue)
    }

    pw.expect(pw.env.get("secretHeaderValueFromPreReqScript")).toBe("secret-header-value")
})

pw.test("Successfully parses secret variable holding the request body value", () => {
    const secretBodyValue = pw.env.get("secretBodyValue")
    pw.expect(secretBodyValue).toBe("secret-body-value")

    if (secretBodyValue) {
      pw.expect(pw.response.body.json.secretBodyKey).toBe(secretBodyValue)
    }

    pw.expect(pw.env.get("secretBodyValueFromPreReqScript")).toBe("secret-body-value")
})

pw.test("Successfully parses secret variable holding the query param value", () => {
    const secretQueryParamValue = pw.env.get("secretQueryParamValue")
    pw.expect(secretQueryParamValue).toBe("secret-query-param-value")

    if (secretQueryParamValue) {
      pw.expect(pw.response.body.args.secretQueryParamKey).toBe(secretQueryParamValue)
    }

    pw.expect(pw.env.get("secretQueryParamValueFromPreReqScript")).toBe("secret-query-param-value")
})

pw.test("Successfully parses secret variables holding basic auth credentials", () => {
	const secretBasicAuthUsername = pw.env.get("secretBasicAuthUsername")
  	const secretBasicAuthPassword = pw.env.get("secretBasicAuthPassword")

    pw.expect(secretBasicAuthUsername).toBe("test-user")
    pw.expect(secretBasicAuthPassword).toBe("test-pass")

    if (secretBasicAuthUsername && secretBasicAuthPassword) {
      const { authenticated, user } = pw.response.body
      pw.expect(authenticated).toBe(true)
      pw.expect(user).toBe(secretBasicAuthUsername)
    }
});

pw.test("Successfully parses secret variable holding the bearer token", () => {
    const secretBearerToken = pw.env.get("secretBearerToken")
  const preReqSecretBearerToken = pw.env.get("preReqSecretBearerToken")

    pw.expect(secretBearerToken).toBe("test-token")

    if (secretBearerToken) { 
      pw.expect(pw.response.body.token).toBe(secretBearerToken)
      pw.expect(preReqSecretBearerToken).toBe("test-token")
    }
});

pw.test("Returns an empty string if the value for a secret environment variable is not found in the system environment", () => {
  pw.expect(pw.env.get("nonExistentValueInSystemEnv")).toBe("")
})
